"use client";

import { useState, useContext } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import AuthContext from "../../context/AuthContext";
import { User, Clipboard, File, Medal, LogOut, Settings, Home, LogIn, UserPlus } from "lucide-react";
import ThemeToggle from "./ThemeToggle";

const Navbar = () => {
  const { isAuthenticated, user, logout, isAdmin, isExaminer } = useContext(AuthContext);
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  // Determine active link
  const isActive = (path) => {
    return location.pathname === path;
  };

  // Get dashboard link based on user role
  const getDashboardLink = () => {
    if (isAdmin) return "/admin";
    if (isExaminer) return "/examiner";
    return "/dashboard";
  };

  return (
    <nav className="bg-white dark:bg-gray-900 shadow-md">
      <div className="container mx-auto px-4 py-4 flex items-center justify-between">
        <Link to={isAuthenticated ? getDashboardLink() : "/"} className="text-2xl font-bold text-blue-600 dark:text-neon-blue">
          ExamPlatform
        </Link>

        <button className="block md:hidden text-gray-900 dark:text-gray-200" onClick={toggleMenu}>
          â˜°
        </button>

        <ul className={`flex-col md:flex-row md:flex ${isMenuOpen ? "flex" : "hidden"} md:flex items-center space-y-4 md:space-y-0 md:space-x-6`}>
          {isAuthenticated ? (
            <>
              <li className="navbar-item">
                <Link to={getDashboardLink()} className={`navbar-link ${isActive(getDashboardLink()) ? "active" : ""}`}>
                  <Home className="inline-block mr-1" size={20} /> Dashboard
                </Link>
              </li>

              {/* Student Links */}
              {!isAdmin && !isExaminer && (
                <>
                  <li className="navbar-item">
                    <Link to="/exams" className={`navbar-link ${isActive("/exams") ? "active" : ""}`}>
                      <Clipboard className="inline-block mr-1" size={20} /> Exams
                    </Link>
                  </li>
                  <li className="navbar-item">
                    <Link to="/certificates" className={`navbar-link ${isActive("/certificates") ? "active" : ""}`}>
                      <Medal className="inline-block mr-1" size={20} /> Certificates
                    </Link>
                  </li>
                </>
              )}

              {/* Examiner Links */}
              {(isExaminer || isAdmin) && (
                <>
                  <li className="navbar-item">
                    <Link to="/examiner/exams" className={`navbar-link ${isActive("/examiner/exams") ? "active" : ""}`}>
                      <Clipboard className="inline-block mr-1" size={20} /> Manage Exams
                    </Link>
                  </li>
                  <li className="navbar-item">
                    <Link to="/examiner/questions" className={`navbar-link ${isActive("/examiner/questions") ? "active" : ""}`}>
                      <File className="inline-block mr-1" size={20} /> Question Bank
                    </Link>
                  </li>
                  <li className="navbar-item">
                    <Link to="/examiner/submissions" className={`navbar-link ${isActive("/examiner/submissions") ? "active" : ""}`}>
                      <File className="inline-block mr-1" size={20} /> Submissions
                    </Link>
                  </li>
                </>
              )}

              {/* Admin Links */}
              {isAdmin && (
                <>
                  <li className="navbar-item">
                    <Link to="/admin/users" className={`navbar-link ${isActive("/admin/users") ? "active" : ""}`}>
                      <User className="inline-block mr-1" size={20} /> Users
                    </Link>
                  </li>
                  <li className="navbar-item">
                    <Link to="/admin/settings" className={`navbar-link ${isActive("/admin/settings") ? "active" : ""}`}>
                      <Settings className="inline-block mr-1" size={20} /> Settings
                    </Link>
                  </li>
                </>
              )}

              <li className="navbar-item">
                <Link to="/profile" className={`navbar-link ${isActive("/profile") ? "active" : ""}`}>
                  <User className="inline-block mr-1" size={20} /> Profile
                </Link>
              </li>
              <li className="navbar-item">
                <button onClick={handleLogout} className="navbar-link">
                  <LogOut className="inline-block mr-1" size={20} /> Logout
                </button>
              </li>
              {user && (
                <li className="navbar-item">
                  <span className="navbar-text">
                    <strong>{user.name}</strong> ({user.role})
                  </span>
                </li>
              )}
            </>
          ) : (
            <>
              <li className="navbar-item">
                <Link to="/login" className={`navbar-link ${isActive("/login") ? "active" : ""}`}>
                  <LogIn className="inline-block mr-1" size={20} /> Login
                </Link>
              </li>
              <li className="navbar-item">
                <Link to="/register" className={`navbar-link ${isActive("/register") ? "active" : ""}`}>
                  <UserPlus className="inline-block mr-1" size={20} /> Register
                </Link>
              </li>
            </>
          )}
          <li className="navbar-item">
            <ThemeToggle />
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
