"use client";

import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { authAPI, examsAPI, submissionsAPI, certificatesAPI } from "../../services/api";
import Loader from "../../components/common/Loader";

const AdminDashboard = () => {
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalExams: 0,
    totalSubmissions: 0,
    totalCertificates: 0,
    studentCount: 0,
    examinerCount: 0,
    adminCount: 0,
  });
  const [recentUsers, setRecentUsers] = useState([]);
  const [recentExams, setRecentExams] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    setLoading(true);
    try {
      // Fetch users
      const usersResponse = await authAPI.getUsers();
      const users = usersResponse.data.data || [];

      // Fetch exams
      const examsResponse = await examsAPI.getExams();
      const exams = examsResponse.data.data || [];

      // Fetch submissions
      const submissionsResponse = await submissionsAPI.getSubmissions();
      const submissions = submissionsResponse.data.data || [];

      // Fetch certificates
      const certificatesResponse = await certificatesAPI.getCertificates();
      const certificates = certificatesResponse.data.data || [];

      // Calculate stats
      const studentCount = users.filter((user) => user.role === "student").length;
      const examinerCount = users.filter((user) => user.role === "examiner").length;
      const adminCount = users.filter((user) => user.role === "admin").length;

      setStats({
        totalUsers: users.length,
        totalExams: exams.length,
        totalSubmissions: submissions.length,
        totalCertificates: certificates.length,
        studentCount,
        examinerCount,
        adminCount,
      });

      // Get recent users
      const sortedUsers = [...users].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)).slice(0, 5);
      setRecentUsers(sortedUsers);

      // Get recent exams
      const sortedExams = [...exams].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt)).slice(0, 5);
      setRecentExams(sortedExams);

      setLoading(false);
    } catch (error) {
      console.error("Error fetching dashboard data:", error);
      toast.error("Failed to load dashboard data");
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return <Loader />;
  }

  return (
    <div className="container mx-auto py-8">
      <h1 className="mb-8 text-4xl font-bold text-blue-600">Admin Dashboard</h1>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
        <div className="bg-white shadow-lg rounded-lg p-6 text-center">
          <h5 className="text-lg font-medium text-gray-700">Total Users</h5>
          <h2 className="text-4xl font-bold text-blue-600">{stats.totalUsers}</h2>
          <Link to="/admin/users" className="mt-4 inline-block bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
            Manage Users
          </Link>
        </div>
        <div className="bg-white shadow-lg rounded-lg p-6 text-center">
          <h5 className="text-lg font-medium text-gray-700">Total Exams</h5>
          <h2 className="text-4xl font-bold text-blue-600">{stats.totalExams}</h2>
        </div>
        <div className="bg-white shadow-lg rounded-lg p-6 text-center">
          <h5 className="text-lg font-medium text-gray-700">Total Submissions</h5>
          <h2 className="text-4xl font-bold text-blue-600">{stats.totalSubmissions}</h2>
        </div>
        <div className="bg-white shadow-lg rounded-lg p-6 text-center">
          <h5 className="text-lg font-medium text-gray-700">Total Certificates</h5>
          <h2 className="text-4xl font-bold text-blue-600">{stats.totalCertificates}</h2>
        </div>
      </div>

      {/* User Distribution */}
      <div className="bg-white shadow-lg rounded-lg p-6 mb-8">
        <h5 className="text-lg font-medium text-gray-700 mb-4">User Distribution</h5>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
          <div className="p-4 bg-gray-100 rounded-lg">
            <h3 className="text-2xl font-bold text-blue-600">{stats.studentCount}</h3>
            <p className="text-gray-700">Students</p>
          </div>
          <div className="p-4 bg-gray-100 rounded-lg">
            <h3 className="text-2xl font-bold text-blue-600">{stats.examinerCount}</h3>
            <p className="text-gray-700">Examiners</p>
          </div>
          <div className="p-4 bg-gray-100 rounded-lg">
            <h3 className="text-2xl font-bold text-blue-600">{stats.adminCount}</h3>
            <p className="text-gray-700">Administrators</p>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="bg-white shadow-lg rounded-lg p-6 mb-8">
        <h5 className="text-lg font-medium text-gray-700 mb-4">Quick Actions</h5>
        <div className="flex flex-wrap gap-4">
          <Link to="/admin/users/create" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
            Create New User
          </Link>
          <Link to="/admin/settings" className="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600 transition">
            System Settings
          </Link>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Recent Users */}
        <div className="bg-white shadow-lg rounded-lg p-6">
          <div className="flex justify-between items-center mb-4">
            <h5 className="text-lg font-medium text-gray-700">Recent Users</h5>
            <Link to="/admin/users" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
              View All
            </Link>
          </div>
          {recentUsers.length > 0 ? (
            <div className="divide-y divide-gray-200">
              {recentUsers.map((user) => (
                <Link
                  key={user._id}
                  to={`/admin/users/${user._id}/edit`}
                  className="block py-4 hover:bg-gray-100 transition"
                >
                  <div className="flex justify-between items-center">
                    <h6 className="text-lg font-medium text-gray-700">{user.name}</h6>
                    <span className={`px-3 py-1 rounded-full text-sm ${user.role === 'admin' ? 'bg-red-500 text-white' : user.role === 'examiner' ? 'bg-yellow-500 text-white' : 'bg-green-500 text-white'}`}>
                      {user.role.toUpperCase()}
                    </span>
                  </div>
                  <p className="text-gray-600">{user.email}</p>
                  <small className="text-gray-500">Joined on {formatDate(user.createdAt)}</small>
                </Link>
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-500">No users found</p>
          )}
        </div>

        {/* Recent Exams */}
        <div className="bg-white shadow-lg rounded-lg p-6">
          <div className="flex justify-between items-center mb-4">
            <h5 className="text-lg font-medium text-gray-700">Recent Exams</h5>
            <Link to="/examiner/exams" className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 transition">
              View All
            </Link>
          </div>
          {recentExams.length > 0 ? (
            <div className="divide-y divide-gray-200">
              {recentExams.map((exam) => (
                <div key={exam._id} className="block py-4 hover:bg-gray-100 transition">
                  <div className="flex justify-between items-center">
                    <h6 className="text-lg font-medium text-gray-700">{exam.title}</h6>
                    <span
                      className={`px-3 py-1 rounded-full text-sm ${
                        exam.status === "published"
                          ? "bg-green-500 text-white"
                          : exam.status === "draft"
                          ? "bg-yellow-500 text-white"
                          : "bg-red-500 text-white"
                      }`}
                    >
                      {exam.status.toUpperCase()}
                    </span>
                  </div>
                  <p className="text-gray-600">{exam.description.substring(0, 100)}...</p>
                  <small className="text-gray-500">
                    Created by {exam.createdBy.name} on {formatDate(exam.createdAt)}
                  </small>
                </div>
              ))}
            </div>
          ) : (
            <p className="text-center text-gray-500">No exams found</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
