"use client";

import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { authAPI } from "../../services/api";
import Loader from "../../components/common/Loader";
import Modal from "../../components/common/Modal";
import Pagination from "../../components/common/Pagination";
import {
  UserPlus,
  Edit,
  Trash2,
  Search,
  Filter,
  Users,
  ChevronDown,
  School,
  Shield,
  CheckCircle,
  Mail,
  Calendar,
} from "lucide-react";

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filter, setFilter] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [isFilterOpen, setIsFilterOpen] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, [filter, currentPage]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await authAPI.getUsers();
      let filteredUsers = response.data.data || [];

      if (filter !== "all") {
        filteredUsers = filteredUsers.filter((user) => user.role === filter);
      }

      if (searchTerm) {
        filteredUsers = filteredUsers.filter(
          (user) =>
            user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
            (user.institution && user.institution.toLowerCase().includes(searchTerm.toLowerCase()))
        );
      }

      setUsers(filteredUsers);
      setTotalPages(Math.ceil(filteredUsers.length / 10));
    } catch (error) {
      console.error("Error fetching users:", error);
      toast.error("Failed to load users");
    }
    setLoading(false);
  };

  const handleFilterChange = (value) => {
    setFilter(value);
    setCurrentPage(1);
    setIsFilterOpen(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchUsers();
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const confirmDelete = (user) => {
    setUserToDelete(user);
    setShowDeleteModal(true);
  };

  const handleDeleteUser = async () => {
    if (!userToDelete) return;
    try {
      await authAPI.deleteUser(userToDelete._id);
      toast.success("User deleted successfully");
      setShowDeleteModal(false);
      fetchUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
      toast.error(error.response?.data?.message || "Failed to delete user");
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString(undefined, {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const indexOfLastUser = currentPage * 10;
  const indexOfFirstUser = indexOfLastUser - 10;
  const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);

  const getRoleColor = (role) => {
    switch (role) {
      case "admin":
        return "bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300";
      case "examiner":
        return "bg-amber-100 text-amber-800 dark:bg-amber-900/30 dark:text-amber-300";
      default:
        return "bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300";
    }
  };

  const getRoleIcon = (role) => {
    switch (role) {
      case "admin":
        return <Shield className="h-4 w-4 mr-1" />;
      case "examiner":
        return <CheckCircle className="h-4 w-4 mr-1" />;
      default:
        return <School className="h-4 w-4 mr-1" />;
    }
  };

  if (loading) {
    return <Loader />;
  }

  return (
    <div className="container mx-auto px-4 py-10 max-w-7xl">
      <div className="mb-8 flex justify-between items-center">
        <h1 className="text-3xl font-bold">User Management</h1>
        <Link
          to="/admin/users/create"
          className="px-4 py-2 bg-blue-600 text-white rounded-md flex items-center"
        >
          <UserPlus className="mr-2" />
          Create User
        </Link>
      </div>

      <div className="flex gap-4 mb-6">
        <form onSubmit={handleSearch} className="flex-grow relative">
          <Search className="absolute left-3 top-2.5 text-gray-400" />
          <input
            type="text"
            placeholder="Search users..."
            value={searchTerm}
            onChange={handleSearchChange}
            className="pl-10 py-2 w-full border rounded-md"
          />
        </form>
        <button onClick={() => setIsFilterOpen(!isFilterOpen)} className="px-4 py-2 border rounded-md">
          <Filter className="mr-2" />
          Filter
        </button>
        {isFilterOpen && (
          <div className="absolute bg-white border rounded-md shadow-md">
            <button onClick={() => handleFilterChange("all")}>All Users</button>
            <button onClick={() => handleFilterChange("student")}>Students</button>
            <button onClick={() => handleFilterChange("examiner")}>Examiners</button>
            <button onClick={() => handleFilterChange("admin")}>Admins</button>
          </div>
        )}
      </div>

      {users.length === 0 ? (
        <p>No users found.</p>
      ) : (
        <table className="w-full border rounded-md">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Created At</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {currentUsers.map((user) => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td className={getRoleColor(user.role)}>
                  {getRoleIcon(user.role)}
                  {user.role}
                </td>
                <td>{formatDate(user.createdAt)}</td>
                <td>
                  <Link to={`/admin/users/edit/${user._id}`}><Edit /></Link>
                  <button onClick={() => confirmDelete(user)}><Trash2 /></button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />

      {showDeleteModal && (
        <Modal title="Confirm Delete" onClose={() => setShowDeleteModal(false)}>
          <p>Are you sure you want to delete {userToDelete?.name}?</p>
          <button onClick={handleDeleteUser}>Yes, Delete</button>
          <button onClick={() => setShowDeleteModal(false)}>Cancel</button>
        </Modal>
      )}
    </div>
  );
};

export default UserManagement;
