"use client";

import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { toast } from "react-toastify";
import { authAPI } from "../../services/api";
import Loader from "../../components/common/Loader";
import Modal from "../../components/common/Modal";
import Pagination from "../../components/common/Pagination";
import {
  UserPlus,
  Edit,
  Trash2,
  Search,
  Filter,
  Users,
  ChevronDown,
  School,
  Shield,
  CheckCircle,
  Mail,
  Calendar,
} from "lucide-react";

const UserManagement = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filter, setFilter] = useState("all");
  const [searchTerm, setSearchTerm] = useState("");
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [userToDelete, setUserToDelete] = useState(null);
  const [isFilterOpen, setIsFilterOpen] = useState(false);

  useEffect(() => {
    fetchUsers();
  }, [filter, currentPage]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const response = await authAPI.getUsers();
      let filteredUsers = response.data.data || [];

      if (filter !== "all") {
        filteredUsers = filteredUsers.filter((user) => user.role === filter);
      }

      if (searchTerm) {
        filteredUsers = filteredUsers.filter(
          (user) =>
            user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            user.email.toLowerCase().includes(searchTerm.toLowerCase()) ||
            (user.institution && user.institution.toLowerCase().includes(searchTerm.toLowerCase()))
        );
      }

      setUsers(filteredUsers);
      setTotalPages(Math.ceil(filteredUsers.length / 10));
    } catch (error) {
      console.error("Error fetching users:", error);
      toast.error("Failed to load users");
    }
    setLoading(false);
  };

  const handleFilterChange = (value) => {
    setFilter(value);
    setCurrentPage(1);
    setIsFilterOpen(false);
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchUsers();
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  const confirmDelete = (user) => {
    setUserToDelete(user);
    setShowDeleteModal(true);
  };

  const handleDeleteUser = async () => {
    if (!userToDelete) return;
    try {
      await authAPI.deleteUser(userToDelete._id);
      toast.success("User deleted successfully");
      setShowDeleteModal(false);
      fetchUsers();
    } catch (error) {
      console.error("Error deleting user:", error);
      toast.error(error.response?.data?.message || "Failed to delete user");
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString(undefined, {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const indexOfLastUser = currentPage * 10;
  const indexOfFirstUser = indexOfLastUser - 10;
  const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);

  const getRoleColor = (role) => {
    switch (role) {
      case "admin":
        return "bg-red-100 text-red-800 dark:bg-red-900/30 dark:text-red-300";
      case "examiner":
        return "bg-amber-100 text-amber-800 dark:bg-amber-900/30 dark:text-amber-300";
      default:
        return "bg-blue-100 text-blue-800 dark:bg-blue-900/30 dark:text-blue-300";
    }
  };

  const getRoleIcon = (role) => {
    switch (role) {
      case "admin":
        return <Shield className="h-4 w-4 mr-1" />;
      case "examiner":
        return <CheckCircle className="h-4 w-4 mr-1" />;
      default:
        return <School className="h-4 w-4 mr-1" />;
    }
  };

  if (loading) {
    return <Loader />;
  }

  return (
    <div className="container mx-auto px-4 py-10 max-w-7xl">
      <div className="mb-8 flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900 dark:text-white">User Management</h1>
        <Link
          to="/admin/users/create"
          className="px-4 py-2 bg-blue-600 text-white rounded-md flex items-center hover:bg-blue-700 transition"
        >
          <UserPlus className="mr-2" />
          Create User
        </Link>
      </div>

      <div className="flex gap-4 mb-6">
        <form onSubmit={handleSearch} className="flex-grow relative">
          <Search className="absolute left-3 top-2.5 text-gray-400" />
          <input
            type="text"
            placeholder="Search users..."
            value={searchTerm}
            onChange={handleSearchChange}
            className="pl-10 py-2 w-full border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
        </form>
        <div className="relative">
          <button
            onClick={() => setIsFilterOpen(!isFilterOpen)}
            className="px-4 py-2 border rounded-md flex items-center hover:bg-gray-100 dark:hover:bg-gray-700 transition"
          >
            <Filter className="mr-2" />
            Filter
            <ChevronDown className="ml-1" />
          </button>
          {isFilterOpen && (
            <div className="absolute right-0 mt-2 w-48 bg-white dark:bg-gray-800 border rounded-md shadow-md">
              <button
                onClick={() => handleFilterChange("all")}
                className="block px-4 py-2 text-left w-full hover:bg-gray-100 dark:hover:bg-gray-700 transition"
              >
                All Users
              </button>
              <button
                onClick={() => handleFilterChange("student")}
                className="block px-4 py-2 text-left w-full hover:bg-gray-100 dark:hover:bg-gray-700 transition"
              >
                Students
              </button>
              <button
                onClick={() => handleFilterChange("examiner")}
                className="block px-4 py-2 text-left w-full hover:bg-gray-100 dark:hover:bg-gray-700 transition"
              >
                Examiners
              </button>
              <button
                onClick={() => handleFilterChange("admin")}
                className="block px-4 py-2 text-left w-full hover:bg-gray-100 dark:hover:bg-gray-700 transition"
              >
                Admins
              </button>
            </div>
          )}
        </div>
      </div>

      {users.length === 0 ? (
        <p className="text-gray-600 dark:text-gray-400">No users found.</p>
      ) : (
        <div className="overflow-x-auto">
          <table className="w-full border rounded-md">
            <thead className="bg-gray-100 dark:bg-gray-700">
              <tr>
                <th className="px-4 py-2 text-left">Name</th>
                <th className="px-4 py-2 text-left">Email</th>
                <th className="px-4 py-2 text-left">Role</th>
                <th className="px-4 py-2 text-left">Created At</th>
                <th className="px-4 py-2 text-left">Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentUsers.map((user) => (
                <tr key={user._id} className="border-t">
                  <td className="px-4 py-2">{user.name}</td>
                  <td className="px-4 py-2">{user.email}</td>
                  <td className={`px-4 py-2 ${getRoleColor(user.role)} flex items-center`}>
                    {getRoleIcon(user.role)}
                    {user.role}
                  </td>
                  <td className="px-4 py-2">{formatDate(user.createdAt)}</td>
                  <td className="px-4 py-2 flex items-center space-x-2">
                    <Link to={`/admin/users/edit/${user._id}`} className="text-blue-600 hover:text-blue-800 transition">
                      <Edit />
                    </Link>
                    <button onClick={() => confirmDelete(user)} className="text-red-600 hover:text-red-800 transition">
                      <Trash2 />
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />

      {showDeleteModal && (
        <Modal title="Confirm Delete" onClose={() => setShowDeleteModal(false)}>
          <p className="mb-4">Are you sure you want to delete {userToDelete?.name}?</p>
          <div className="flex justify-end space-x-2">
            <button
              onClick={handleDeleteUser}
              className="px-4 py-2 bg-red-600 text-white rounded-md hover:bg-red-700 transition"
            >
              Yes, Delete
            </button>
            <button
              onClick={() => setShowDeleteModal(false)}
              className="px-4 py-2 bg-gray-300 dark:bg-gray-700 text-gray-900 dark:text-gray-200 rounded-md hover:bg-gray-400 dark:hover:bg-gray-600 transition"
            >
              Cancel
            </button>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default UserManagement;
