"use client"

import { useState, useEffect } from "react"
import { useParams, useNavigate } from "react-router-dom"
import { toast } from "react-toastify"
import { authAPI } from "../../services/api"
import Loader from "../../components/common/Loader"

const EditUser = () => {
  const { id } = useParams()
  const navigate = useNavigate()
  const [loading, setLoading] = useState(true)
  const [saving, setSaving] = useState(false)
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    role: "student",
    institution: "",
  })
  const [formErrors, setFormErrors] = useState({})

  useEffect(() => {
    fetchUserData()
  }, [id])

  const fetchUserData = async () => {
    setLoading(true)
    try {
      // In a real implementation, you would have an API endpoint to get a single user
      // For now, we'll get all users and find the one we need
      const response = await authAPI.getUsers()
      const users = response.data.data || []
      const user = users.find((u) => u._id === id)

      if (!user) {
        toast.error("User not found")
        navigate("/admin/users")
        return
      }

      setFormData({
        name: user.name,
        email: user.email,
        role: user.role,
        institution: user.institution || "",
      })

      setLoading(false)
    } catch (error) {
      console.error("Error fetching user:", error)
      toast.error("Failed to load user data")
      navigate("/admin/users")
    }
  }

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData({ ...formData, [name]: value })

    // Clear error when user types
    if (formErrors[name]) {
      setFormErrors({ ...formErrors, [name]: "" })
    }
  }

  const validateForm = () => {
    const errors = {}
    if (!formData.name) errors.name = "Name is required"
    if (!formData.email) errors.email = "Email is required"

    setFormErrors(errors)
    return Object.keys(errors).length === 0
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!validateForm()) {
      toast.error("Please fix the errors in the form")
      return
    }

    setSaving(true)
    try {
      await authAPI.updateUser(id, formData)
      toast.success("User updated successfully")
      navigate("/admin/users")
    } catch (error) {
      console.error("Error updating user:", error)
      toast.error(error.response?.data?.message || "Failed to update user")
      setSaving(false)
    }
  }

  if (loading) {
    return <Loader />
  }

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h1>Edit User</h1>
        <button className="btn btn-secondary" onClick={() => navigate("/admin/users")}>
          Back to Users
        </button>
      </div>

      <div className="card">
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group mb-3">
              <label htmlFor="name">Full Name</label>
              <input
                type="text"
                className={`form-control ${formErrors.name ? "is-invalid" : ""}`}
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                placeholder="Enter full name"
              />
              {formErrors.name && <div className="invalid-feedback">{formErrors.name}</div>}
            </div>

            <div className="form-group mb-3">
              <label htmlFor="email">Email Address</label>
              <input
                type="email"
                className={`form-control ${formErrors.email ? "is-invalid" : ""}`}
                id="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter email address"
              />
              {formErrors.email && <div className="invalid-feedback">{formErrors.email}</div>}
            </div>

            <div className="form-group mb-3">
              <label htmlFor="role">Role</label>
              <select className="form-select" id="role" name="role" value={formData.role} onChange={handleChange}>
                <option value="student">Student</option>
                <option value="examiner">Examiner</option>
                <option value="admin">Administrator</option>
              </select>
            </div>

            <div className="form-group mb-3">
              <label htmlFor="institution">Institution (Optional)</label>
              <input
                type="text"
                className="form-control"
                id="institution"
                name="institution"
                value={formData.institution}
                onChange={handleChange}
                placeholder="Enter institution name"
              />
            </div>

            <div className="d-flex justify-content-end">
              <button type="button" className="btn btn-secondary me-2" onClick={() => navigate("/admin/users")}>
                Cancel
              </button>
              <button type="submit" className="btn btn-primary" disabled={saving}>
                {saving ? "Saving..." : "Save Changes"}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  )
}

export default EditUser
